        """{"genres":[{"id":28,"name":"Action"},{"id":12,"name":"Adventure"},{"id":16,"name":"Animation"},{"id":35,"name":"Comedy"},{"id":80,"name":"Crime"},{"id":99,"name":"Documentary"},{"id":18,"name":"Drama"},{"id":10751,"name":"Family"},{"id":14,"name":"Fantasy"},{"id":36,"name":"History"},{"id":27,"name":"Horror"},{"id":10402,"name":"Music"},{"id":9648,"name":"Mystery"},{"id":10749,"name":"Romance"},{"id":878,"name":"Science Fiction"},{"id":10770,"name":"TV Movie"},{"id":53,"name":"Thriller"},{"id":10752,"name":"War"},{"id":37,"name":"Western"}]}"""
    private const val dummyGenresTvShowsData =
        """{"genres":[{"id":10759,"name":"Action & Adventure"},{"id":16,"name":"Animation"},{"id":35,"name":"Comedy"},{"id":80,"name":"Crime"},{"id":99,"name":"Documentary"},{"id":18,"name":"Drama"},{"id":10751,"name":"Family"},{"id":10762,"name":"Kids"},{"id":9648,"name":"Mystery"},{"id":10763,"name":"News"},{"id":10764,"name":"Reality"},{"id":10765,"name":"Sci-Fi & Fantasy"},{"id":10766,"name":"Soap"},{"id":10767,"name":"Talk"},{"id":10768,"name":"War & Politics"},{"id":37,"name":"Western"}]}"""

    fun generateDummyMoviesAndTv(): Pair<MovieEntity, TvShowEntity> {
        val data = searchGenre(
            gson.fromJson(dummyMoviesData, MovieEntity::class.java),
            gson.fromJson(dummyTvShowsData, TvShowEntity::class.java)
        )
        return Pair(data.first, data.second)
    }

    fun generateDummyMovieTvGenres(): Pair<GenreEntity, GenreEntity> {
        return Pair(
            gson.fromJson(dummyGenresMoviesData, GenreEntity::class.java),
            gson.fromJson(dummyGenresTvShowsData, GenreEntity::class.java)
        )
    }

    private fun searchGenre(
        movieEntity: MovieEntity,
        tvShowEntity: TvShowEntity
    ): Pair<MovieEntity, TvShowEntity> {

        moviesGenre = gson.fromJson(dummyGenresMoviesData, GenreEntity::class.java)
        tvShowsGenre = gson.fromJson(dummyGenresTvShowsData, GenreEntity::class.java)

        movieEntity.movies?.forEach { movie ->
            movie.genres = ArrayList()
            movie.genreIds?.forEach { id ->
                moviesGenre.genres?.forEach {
                    if (id == it.id.toString()) {
                        it.name?.let { genreName -> movie.genres?.add(genreName) }
                    }
                }
            }
        }
        tvShowEntity.tvShow?.forEach { tvShows ->
            tvShows.genres = ArrayList()
            tvShows.genreIds?.forEach { id ->
                tvShowsGenre.genres?.forEach { it ->
                    if (id == it.id.toString()) {
                        it.name?.let { genreName -> tvShows.genres?.add(genreName) }
                    }
                }
            }
        }
        return Pair(movieEntity, tvShowEntity)
    }
